<?php if ($_GET["colour1"]) { $colour1 = $_GET["colour1"]; } else { $colour1 = "black"; } if ($_GET["colour2"]) { $colour2 = $_GET["colour2"]; } else { $colour2 = "black"; } if ($_GET["font1"]) { $font1 = $_GET["font1"]; } else { $font1 = "Courier New"; } if ($_GET["font2"]) { $font2 = $_GET["font2"]; } else { $font2 = "Courier New"; 

} ?> <!DOCTYPE html> <html> <head> <meta charset="UTF-8"> <title>lr4</title> <style type="text/css"> canvas { border: 1px solid black; } </style> </head> <body onload="draw();"> <canvas id="canvas" width="370" height="200"></canvas> <script type="text/javascript"> function draw() { var canvas = document.getElementById('canvas'); if (canvas.getContext) { var ctx = canvas.getContext('2d'); <!— отрисовка графическими примитивами с использованием Canvas —> context.beginPath(); context.moveTo(200, 100); context.lineTo(150, 100); context.lineTo(150, 200); context.lineTo(200, 200); context.lineTo(200, 150); context.lineTo(175, 150); context.moveTo(250, 100); context.lineTo(250, 200); context.moveTo(350, 100); context.lineTo(300, 100); context.lineTo(300, 150); context.lineTo(350, 150); context.lineTo(350, 200); 
context.lineTo(300, 200); context.stroke(); <!— отрисовка текста с использованием Canvas —> ctx.fillStyle = "<?php echo $colour1; ?>"; ctx.strokeStyle = "#000"; ctx.font = "<?php echo $font1; ?>"; ctx.textBaseline = "bottom"; ctx.fillText("ГИС", 200, 100); } } </script> <svg width="370" height="150"> <!— отрисовка графическими примитивами с использованием SVG —> <line x1="150" y1="25" x2="100" y2="25" stroke="black" stroke-width="1" /> <line x1="100" y1="25" x2="100" y2="75" stroke="black" stroke-width="1" /> <line x1="100" y1="75" x2="150" y2="75" stroke="black" stroke-width="1" /> <line x1="150" y1="75" x2="150" y2="50" stroke="black" stroke-width="1" /> <line x1="150" y1="50" x2="125" y2="50" stroke="black" stroke-width="1" /> <line x1="200" y1="25" x2="200" y2="75" stroke="black" stroke-width="1" /> <line x1="300" y1="25" x2="250" y2="25" stroke="black" stroke-width="1" /> <line x1="250" y1="25" x2="250" y2="50" stroke="black" stroke-width="1" /> <line x1="250" y1="50" x2="300" y2="50" stroke="black" stroke-width="1" /> <line x1="300" y1="50" x2="300" y2="75" stroke="black" stroke-width="1" /> <line x1="300" y1="75" x2="250" y2="75" stroke="black" stroke-
width="1" /> <!— отрисовка текста с использованием SVG —> <g fill="<?php echo $colour2; ?>" font-family="<?php echo $font2; ?>" > <text x="170" y="100">ГИС</text> </g> </svg> <form method="$_POST" name="filter" action="http://localhost/lr4.php"> <p>цвет шрифта текста canvas</p> <label><input type="radio" name="colour1" value="blue"> синий</label><br> <label><input type="radio" name="colour1" value="yellow"> жёлтый</label><br> <p>шрифт текста canvas</p> <label><input type="radio" name="font1" value="Courier New"> courier new</label><br> <label><input type="radio" name="font1" value="Arial"> arial</label><br> <p>цвет шрифта текста svg</p> <label><input type="radio" name="colour2" value="blue"> синий</label><br> <label><input type="radio" name="colour2" value="yellow"> жёлтый</label><br> <p>шрифт текста canvas</p> <label><input type="radio" name="font2" value="Courier New"> courier new</label><br> <label><input type="radio" name="font2" value="Arial"> arial</label><br> <input type="submit" value="применить"> </form> </body> </html> 
